body{
    padding: 2rem;
};

nav{
    display:flex;
    align-items: center;
    justify-content: space-between;

    .locator{
        display: flex;
        align-items: center;

        &__icon{
            width:3rem;
            height:3rem;
            margin-right:5px;
        }
        &__text{
            font-size: 1.1rem;
            text-transform: uppercase;
            color:#f6f6f6;
            letter-spacing: 0.5px;

            @media only screen and (max-width:31.25em){
                display:none;
            }
        }
    }

    .logo{
        width:15rem;
    }

    .bookmark{
        display:flex;
        align-items: center;

        &__icon{
            width:2.5rem;
            height:2.5rem;
            margin-left:5px;
        }
        &__text{
            font-size: 1.1rem;
            text-transform: uppercase;
            color:#f6f6f6;

            @media only screen and (max-width:31.25em){
                display:none;
            }
        }
    }

}

.search{

    .main-search{
        input{
            padding:0.8rem;
            font-size: 1.3rem;
            width:100%;
            outline: none;
            border: none;
            background-color: rgba(255,255,255,0.24);
            backdrop-filter: blur(0.5px);
            z-index: 2;
            color:#FFFFFF;

            &::placeholder{
                color:#FFFFFF;
                letter-spacing: 1px;
                font-weight: 300;
                font-family: 'Montserrat', sans-serif;
                

            }
        }
        form{
            display:flex;
            align-items: center;
        }
        &__icon{
            width:2rem;
            height:2rem
        }
        button{
            background-color: transparent;
            border:none;
            margin-left:-3rem;
            z-index: 5;
            cursor: pointer;
        }
    }

    .search-result{
        margin:2rem 0;

        .search-error{
            p{
                font-size: 1.3rem;
                color:white;
                text-align: center;
                letter-spacing: 0.1rem;

            }
        }
        .city{
            background-color: rgba(0,0,0,0.3);
            display: flex;
            align-items: center;
            justify-content: space-between;
            padding:0.2rem 0.4rem;
            margin-bottom: 1.2rem;
            text-decoration: none;
            transition: display 0.3s;
            
            &:hover{
                background-color: rgba(0,0,0,0.5);
                cursor: pointer;
            }
            &__text{
                color:#F6F6F6;
    
                &--1{
                    font-size: 1.3rem;
                }
                &--2{
                    font-size: 1.1rem;
                }
            }

            &__icon{
                width:4rem;
                height:4rem;
                fill:white;
                color:white;
            }
        }
    }
}
.welcome-text{
    text-align: center;
    font-size: 1.4rem;
    line-height: 1.7rem;
    color:#c4c4c4;
    font-weight: 300;
    text-transform: uppercase;
    margin:8rem 0;
}
.sidespinner{
    display: flex;
    align-items: center;
    justify-content: center;
    
    // margin: 1rem 0;

    &__main{
        border: 0.2rem solid rgba(255,255,255,0.24);
        backdrop-filter: blur(0.5px);
        border-radius: 50%;
        border-top: 0.3rem solid #f6f6f6;
        width:2rem;
        height:2rem;
        margin-right: 0.5rem;
        animation: spin 1s linear infinite;
    }
    &__text{
        color:#f6f6f6;
        font-size: 1.2rem;
    }
}
.spinner{
    display: flex;
    align-items: center;
    justify-content: center;
    flex-direction: column;
    margin: 8rem 0;

    &__main{
        border: 0.3rem solid rgba(255,255,255,0.24);
        backdrop-filter: blur(0.5px);
        border-radius: 50%;
        border-top: 0.3rem solid #f6f6f6;
        width:3.5rem;
        height:3.5rem;
        animation: spin 1s linear infinite;
    }

    &__text{
        color:#f6f6f6;
        margin-top:0.4rem;
        font-size: 1.2rem;

    }


}
.error{
    margin: 8rem 0;
    &__text{
        text-transform:uppercase;
        color:#f6f6f6;
        font-size: 1.3rem;
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
    }

    &__icon{
        height:4rem;
        width:4rem;
        margin-bottom:0.6rem;
        fill:#f6f6f6;
    }
}

